/* Create a bindable list */
var friends = js.bindableList();

/* View */
var FriendView = function(){
    this.template = "#friendRowTemplate";
    this.init = function(viewModel){
        this.bind(viewModel.firstName).to(".firstName");
        this.bind(viewModel.lastName).to(".lastName");
    };
}

js.bind(friends).to("#friendsTable tbody", FriendView);

friends.setValue([
    { firstName: "Joe", lastName: "Bloggs"},
    { firstName: "Fred", lastName: "Bloggs"},
    { firstName: "Bob", lastName: "Soap"}
]);

var employee = js.bindableList();

var View = function(){
    this.template = "#listItemTemplate";
    this.init = function(viewModel){
        this.bind(viewModel.name).to("li");
    };
}
js.bind(employee).to("#employee", View);

var joeBloggs          = { name: "Joe Bloggs" };
var fredBloggs         = { name: "Fred Bloggs" };
var bobSoap            = { name: "Bob Soap" };
var charlieFarnsbarns  = { name: "Charlie Farnsbarns" };
var johnQPublic        = { name: "John Q. Public" };
var joePublic          = { name: "Joe Public" };

employee.add(joeBloggs, fredBloggs, bobSoap);
employee.add(charlieFarnsbarns);
employee.add(johnQPublic);
employee.add(joePublic);

employee.remove(fredBloggs, johnQPublic);

// Create a list
var friendsList = js.bindableList();

// Bind list size to the markup
js.bind(friendsList.count()).to("#friendsCount span");

// Set list value to cause list size refresh
friendsList.setValue(["Friend1", "Friend2", "Friend3"]);
